import { useRef, useState } from "react";

const SimpleInput = (props) => {
  const nameRef = useRef();
  const [enteredName, setEnteredName] = useState("");
  const [enteredNameState, setEnteredNameState] = useState(false);

  const nameInputHandler = (event) => {
    setEnteredName(event.target.value);
    // console.log(enteredName);
  };

  const formSubmmitionHandler = (event) => {
    event.preventDefault();
    if (enteredName.trim().length === 0) {
      setEnteredNameState(true);
      return;
    }
    setEnteredNameState(false);
    console.log(enteredName);
    setEnteredName("");
  };
  return (
    <form onSubmit={formSubmmitionHandler}>
      <div className="form-control">
        <label htmlFor="name">Your Name</label>
        <input
          ref={nameRef}
          onChange={nameInputHandler}
          value={enteredName}
          type="text"
          id="name"
        />
        {enteredNameState && <p className="error-text">Name Not Valid</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
