import { useRef, useState } from "react";

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);
  const enteredNameValid = enteredName.trim !== "";
  const isFormInValid = enteredNameTouched && !enteredNameValid;

  const nameInputChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };
  const nameInputBlurHandler = (event) => {
    setEnteredNameTouched(true);
  };

  const formSubmmitionHandler = (event) => {
    event.preventDefault();
    setEnteredNameTouched(true);
    if (enteredName.trim().length === 0) {
      return;
    }
    setEnteredNameValid(true);
    console.log(enteredName);
    setEnteredName("");
  };

  const formClasses = !isFormInValid ? "form-control" : "form-control invalid";

  return (
    <form onSubmit={formSubmmitionHandler}>
      <div className={formClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
          value={enteredName}
          type="text"
          id="name"
        />
        {isFormInValid && <p className="error-text">Name Not Valid</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
