import { useEffect, useState } from "react";
import useInput from "../hooks/use-input";

const SimpleInput = (props) => {
  const {
    value: enteredName,
    isValid: enteredNameValid,
    hasError: nameInputHasError,
    valueChangeHandler: nameChangeHandler,
    valueBlurHandler: nameBlurHandler,
    reset: reset,
  } = useInput((value) => value.trim() !== "");

  const [enteredEmail, setEnteredEmail] = useState("");
  const [enteredEmailTouched, setEnteredEmailTouched] = useState(false);

  const enteredEmailValid =
    enteredEmail.trim() !== "" && enteredEmail.includes("@");
  const isEmailInValid = enteredEmailTouched && !enteredEmailValid;

  let formIsValid = false;
  if (enteredNameValid && enteredEmailValid) {
    formIsValid = true;
  }

  const emailInputChangeHandler = (event) => {
    setEnteredEmail(event.target.value);
  };
  const emailInputBlurHandler = (event) => {
    setEnteredEmailTouched(true);
  };

  const formSubmmitionHandler = (event) => {
    event.preventDefault();

    if (!enteredNameValid) {
      return;
    }

    reset;
    setEnteredEmail("");
    setEnteredEmailTouched(false);
  };

  const nameClasses = !nameInputHasError
    ? "form-control"
    : "form-control invalid";
  const emailClasses = !isEmailInValid
    ? "form-control"
    : "form-control invalid";

  return (
    <form onSubmit={formSubmmitionHandler}>
      <div className={nameClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          onChange={nameChangeHandler}
          onBlur={nameBlurHandler}
          value={enteredName}
          type="text"
          id="name"
        />
        {nameInputHasError && <p className="error-text">Name Not Valid</p>}
      </div>

      <div className={emailClasses}>
        <label htmlFor="email">Your E-Mail</label>
        <input
          onChange={emailInputChangeHandler}
          onBlur={emailInputBlurHandler}
          value={enteredEmail}
          type="email"
          id="email"
        />
        {isEmailInValid && <p className="error-text">Email Not Valid</p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
