import { useRef, useState } from "react";

const SimpleInput = (props) => {
  const nameRef = useRef();
  const [enteredName, setEnteredName] = useState("");
  const [enteredNameValid, setEnteredNameValid] = useState(true);
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);

  const nameInputHandler = (event) => {
    setEnteredName(event.target.value);
    // console.log(enteredName);
  };

  const formSubmmitionHandler = (event) => {
    event.preventDefault();
    setEnteredNameTouched(true);
    if (enteredName.trim().length === 0) {
      setEnteredNameValid(false);
      return;
    }
    setEnteredNameValid(true);
    console.log(enteredName);
    setEnteredName("");
  };

  const formClasses = enteredNameValid
    ? "form-control"
    : "form-control invalid";

  const isFormInValid = enteredNameTouched && !enteredNameValid;
  return (
    <form onSubmit={formSubmmitionHandler}>
      <div className={formClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          ref={nameRef}
          onChange={nameInputHandler}
          value={enteredName}
          type="text"
          id="name"
        />
        {!isFormValid && <p className="error-text">Name Not Valid</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
