import { useEffect, useState } from "react";

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);
  const [enteredEmail, setEnteredEmail] = useState("");
  const [enteredEmailTouched, setEnteredEmailTouched] = useState(false);

  const enteredNameValid = enteredName.trim() !== "";
  const enteredEmailValid =
    enteredEmail.trim() !== "" && enteredEmail.includes("@");

  const isNameInValid = enteredNameTouched && !enteredNameValid;
  const isEmailInValid = enteredEmailTouched && !enteredEmailValid;

  let formIsValid = false;
  if (enteredNameValid && enteredEmailValid) {
    formIsValid = true;
  }

  const nameInputChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };
  const nameInputBlurHandler = (event) => {
    setEnteredNameTouched(true);
  };

  const emailInputChangeHandler = (event) => {
    setEnteredEmail(event.target.value);
  };
  const emailInputBlurHandler = (event) => {
    setEnteredEmailTouched(true);
  };

  const formSubmmitionHandler = (event) => {
    event.preventDefault();
    setEnteredNameTouched(true);
    if (!enteredNameValid) {
      return;
    }

    setEnteredName("");
    setEnteredNameTouched(false);
    setEnteredEmail("");
    setEnteredEmailTouched(false);
  };

  const nameClasses = !isNameInValid ? "form-control" : "form-control invalid";
  const emailClasses = !isEmailInValid
    ? "form-control"
    : "form-control invalid";

  return (
    <form onSubmit={formSubmmitionHandler}>
      <div className={nameClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
          value={enteredName}
          type="text"
          id="name"
        />
        {isNameInValid && <p className="error-text">Name Not Valid</p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
      <div className={emailClasses}>
        <label htmlFor="email">Your E-Mail</label>
        <input
          onChange={emailInputChangeHandler}
          onBlur={emailInputBlurHandler}
          value={enteredEmail}
          type="email"
          id="email"
        />
        {isEmailInValid && <p className="error-text">Email Not Valid</p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
