import { useEffect, useState } from "react";

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);
  const [enteredEmail, setEnteredEmail] = useState("");
  const [enteredEmailTouched, setEnteredEmailTouched] = useState(false);

  const enteredNameValid = enteredName.trim() !== "";
  const enteredEmailValid =
    enteredEmail.trim() !== "" && enteredEmail.containt("@");

  const isNameValid = enteredNameTouched && !enteredNameValid;
  const isEmailValid = enteredEmailTouched && !enteredEmailValid;

  let formIsValid = false;
  if (enteredNameValid && en) {
    formIsValid = true;
  }

  const nameInputChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };
  const nameInputBlurHandler = (event) => {
    setEnteredNameTouched(true);
  };

  const formSubmmitionHandler = (event) => {
    event.preventDefault();
    setEnteredNameTouched(true);
    if (!enteredNameValid) {
      return;
    }

    setEnteredName("");
    setEnteredNameTouched(false);
  };

  const formClasses = !isFormInValid ? "form-control" : "form-control invalid";

  return (
    <form onSubmit={formSubmmitionHandler}>
      <div className={formClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
          value={enteredName}
          type="text"
          id="name"
        />
        {isFormInValid && <p className="error-text">Name Not Valid</p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
      <div className={formClasses}>
        <label htmlFor="email">Your E-Mail</label>
        <input
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
          value={enteredName}
          type="email"
          id="email"
        />
        {isFormInValid && <p className="error-text">Name Not Valid</p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
