import react, { Fragment, useState } from "react";
import Cart from "./components/Cart/Cart";

import Header from "./components/Layout/Header";
import Meals from "./components/Meals/Meals";

function App() {
  const [openCart, openCartState] = useState(true);
  const cartClickeHAndler = () => {
    openCartState(true);
  };

  return (
    <Fragment>
      {openCart && <Cart onClick={cartClickeHAndler}></Cart>}
      <Header />
      <main>
        <Meals></Meals>
      </main>
    </Fragment>
  );
}

export default App;
